import './MainAboutTop.css';
import {useEffect, useState} from "react";

export default function MainAboutTop (){

    const [isMobileTitle , setIsMobileTitle] = useState(false)

    useEffect(() => {
        const mediaQuery = window.matchMedia('(max-width: 480px)'); // Установите нужный вам медиа-запрос
        const handleResize = (event) => {
            setIsMobileTitle(event.matches);
        };
        mediaQuery.addListener(handleResize);
        setIsMobileTitle(mediaQuery.matches);
        return () => {
            mediaQuery.removeListener(handleResize);
        };
    }, []);


    return(
        <div className='main_about_top'>
            <div className='main_about_top_title'>
                <h1 className='main_about_top_desktop_title'>
                    we are the <br/>
                    <span className='pink_text'>best</span> <br/>
                    company
                </h1>
                <h1 className='main_about_top_mob_title'>
                    we are <br/> the
                    <span className='pink_text'>best</span> <br/>
                    company
                </h1>
            </div>
        </div>
    )
}
