import React, { createContext, useState, useEffect } from 'react';

const LanguageContext = createContext();

export const LanguageProvider = ({ children }) => {
    const [language, setLanguage] = useState('en');
    const [translations, setTranslations] = useState({});

    useEffect(() => {

        let json = [{'navBarItems':'hello'}];
        let jsonUrl = '../../text-data/ua.json';

        // Завантажте переклади з JSON-файлу або використовуйте API-запит для отримання перекладів
        const loadTranslations = async () => {
            console.log('response before')
            const response = await fetch(jsonUrl);
            console.log('response : ' , response)
            const data = await response.json();
            console.log('data : ' , data)

            setTranslations(data);
        };
        loadTranslations().then( r => console.log(r))
    }, [language]);

    const changeLanguage = (newLanguage) => {
        setLanguage(newLanguage);
    };

    return (
        <LanguageContext.Provider value={{ language, translations, changeLanguage }}>
            {children}
        </LanguageContext.Provider>
    );
};

export default LanguageContext;
