import uaJSON from '../text-data/ua.json';
import enJSON from '../text-data/en.json';
const LanguageContext = createContext();

export const LanguageProvider = ({ children }) => {
    const [language, setLanguage] = useState('en');
    const [translations, setTranslations] = useState(uaJSON);

    useEffect(() => {
            console.log(uaJSON)
