            console.log(translations)
    }, [language]);

    const changeLanguage = (newLanguage) => {
        setTranslations(enJSON);
    };

    return (
        <LanguageContext.Provider value={{ language, translations, changeLanguage }}>
            {children}
        </LanguageContext.Provider>
    );
};
export default LanguageContext;
