import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import './contactForm.css'; // Імпорт CSS-файлу зі стилями для плаваючих міток

const validationSchema = Yup.object().shape({
    name: Yup.string().required('Поле "Ім\'я" є обов\'язковим'),
    email: Yup.string().email('Неправильний формат електронної пошти').required('Поле "Електронна пошта" є обов\'язковим'),
    comment: Yup.string().required('Поле "Коментар" є обов\'язковим'),
});

const FloatingLabelForm = () => {
    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            comment: '',
        },
        validationSchema: validationSchema,
        onSubmit: values => {
            alert(JSON.stringify(values, null, 2));
        },
    });

    return (
        <form onSubmit={formik.handleSubmit}>
            <div className="form_box">
                <input
                    className='form_input'
                    id="name"
                    name="name"
                    type="text"
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.name}
                />
                <label htmlFor="name" className={(formik.touched.name && formik.errors.name) ? 'active error' : ''}>Ім'я</label>
                {formik.touched.name && formik.errors.name ? <div className="error-message">{formik.errors.name}</div> : null}
            </div>

            <div className="floating-label-input">
                <input
                    id="email"
                    name="email"
                    type="email"
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.email}
                />
                <label htmlFor="email" className={(formik.touched.email && formik.errors.email) ? 'active error' : ''}>Електронна пошта</label>
                {formik.touched.email && formik.errors.email ? <div className="error-message">{formik.errors.email}</div> : null}
            </div>

            <div className="floating-label-input">
        <textarea
            id="comment"
            name="comment"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.comment}
        />
                <label htmlFor="comment" className={(formik.touched.comment && formik.errors.comment) ? 'active error' : ''}>Коментар</label>
                {formik.touched.comment && formik.errors.comment ? <div className="error-message">{formik.errors.comment}</div> : null}
            </div>

            <button type="submit">Відправити</button>
        </form>
    );
};

export default FloatingLabelForm;
