import React, {useState} from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import './contactForm.css';

const validationSchema = Yup.object().shape({
    name: Yup.string().required('Поле "" є обовязковим'),
    email: Yup.string().email('Неправильний формат електронної пошти').required('Поле "Електронна пошта" є обовязковим'),
    comment: Yup.string().required('Поле "Коментар" є обовязковим'),
});

const FloatingLabelInput = ({ label, type, value, onChange, id, inputClass }) => {
    const [focused, setFocused] = useState(false);

    const handleFocus = () => {
        setFocused(true);
    };

    const handleBlur = () => {
        setFocused(false);
    };

    return (
        <div className='form_box'>
            <label
                className={`form_label ${focused || value ? 'active' : ''}`}
            >{label}</label>
            <input
                className={inputClass}
                id={id}
                type={type}
                value={value}
                onChange={onChange}
                onFocus={handleFocus}
                onBlur={handleBlur}
            />
        </div>
    );
};


const ContactForm = () => {
    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            comment: '',
        },

        validationSchema: validationSchema,
        onSubmit: values => {
            alert(JSON.stringify(values, null, 2));
        },
    });

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [comment, setComment] = useState('');

    const handleNameChange = event => {
        setName(event.target.value);
    };

    const handleEmailChange = event => {
        setEmail(event.target.value);
    };

    const handleCommentChange = event => {
        setComment(event.target.value);
    };


    return (
        <form className='form_submit' onSubmit={formik.handleSubmit}>
               <FloatingLabelInput
                    inputClass={formik.touched.name && formik.errors.name ? 'error form_input' : 'form_input'}
                    id={'name'}
                    label={'name'}
                    type={'name'}
                    value={formik.values.name}

                    />


            <div className='form_box'>
                <label className='form_label' htmlFor="email">E-mail:</label>
                <input
                    className={formik.touched.email && formik.errors.email ? 'error form_input' : 'form_input'}
                    id="email"
                    name="email"
                    type="email"
                    onChange={handleEmailChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.email}
                />
            </div>

            <div className='form_box'>
                <label
                    className={formik.handleBlur && formik.values.comment ? 'active form_label' : 'form_label'}
                    htmlFor="comment">Lorem ipsum dolor:</label>
                <input
                    className={formik.touched.comment && formik.errors.comment ? 'error form_input' : 'form_input'}
                    id="comment"
                    name="comment"
                    onChange={handleCommentChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.comment}
                />
            </div>

            <button className='form_submit_btn' type="submit">Send</button>
        </form>
    );
};

export default ContactForm;
