import React, {useState} from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import './contactForm.css';

const validationSchema = Yup.object().shape({
    name: Yup.string().required('Поле є обовязковим'),
    email: Yup.string().email('Неправильний формат електронної пошти').required('Поле "Електронна пошта" є обовязковим'),
    comment: Yup.string().required('Поле "Коментар" є обовязковим'),
});

const FloatingLabelInput = ({ label, type, value, onChange, id, inputClass }) => {
    const [focused, setFocused] = useState(false);
    const [inputClassName, setInputClassName] = useState('');

    const handleChangeClass = () => {
        setInputClassName(inputClass)
    }
    const handleFocus = () => {
        setFocused(true);
    };

    const handleBlur = () => {
        setFocused(false);
    };

    return (
        <div className='form_box'>
            <label
                className={`form_label ${focused || value ? 'active' : ''}`}
            >{label}</label>
            <input
                className={inputClassName}
                id={id}
                type={type}
                value={value}
                onChange={onChange}
                onFocus={handleFocus}
                onBlur={handleBlur}
            />
        </div>
    );
};


const ContactForm = () => {
    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            comment: '',
        },
        handleChange : e => {
            e.target.value
        },
        validationSchema: validationSchema,
        onSubmit: values => {
            alert(JSON.stringify(values, null, 2));
        },
    });
    const [focused, setFocused] = useState(false);
    const handleFocus = () => {
        setFocused(true);
    };
    const handleBlur = () => {
        setFocused(false);
    };




    return (
        <form className='form_submit' onSubmit={formik.handleSubmit}>
            <div className='form_box'>
                <label
                    className={`form_label ${focused || value ? 'active' : ''}`}
                >Name</label>
                <input
                    className={formik.touched.name && formik.errors.name ? 'error form_input' : 'form_input'}
                    id='name'
                    type='name'
                    value={formik.values.comment}
                    onChange={formik.handleChange}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                />
            </div>

            <button className='form_submit_btn' type="submit">Send</button>
        </form>
    );
};

export default ContactForm;
