import React, { createContext, useState, useEffect } from 'react';
import uaJson from '../text-data/ua.json';
import enJson from '../text-data/en.json';
const LanguageContext = createContext();

export const LanguageProvider = ({ children }) => {
    const [toggleLang, setToggleLang] = useState(true)
    const [language, setLanguage] = useState('en');
    const [translations, setTranslations] = useState(uaJson);
    //
    // useEffect(() => {
    //         setTranslations(uaJson);
    // }, [language]);

    const changeLanguage = (newLanguage) => {
        setTranslations(enJson);
    };

    return (
        <LanguageContext.Provider value={{ language, translations, changeLanguage }}>
            {children}
        </LanguageContext.Provider>
    );
};
export default LanguageContext;
