import React, {createContext, useEffect, useState} from 'react';

export const PageContext = createContext();

export const pageProvider = ({ children }) => {
    const [language, setLanguage] = useState('ua');
    const [translations, setTranslations] = useState({});

    useEffect(() => {
        // Завантажте переклади з JSON-файлу або використовуйте API-запит для отримання перекладів
        const loadTranslations = async () => {
            const response = await fetch(`text-data/${language}.json`);
            const data = await response.json();
            setTranslations(data);
        };

        loadTranslations();
    }, [language]);


    const changeLanguage = (newLanguage) => {
        setLanguage(newLanguage);
    };


    return (
        <PageContext.Provider value={{ language, translations, changeLanguage }}>
            {children}
        </PageContext.Provider>
    );
};

export default PageContext;
